mapper.select:
select 

from tb_exam
where id = #{id,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=INTEGER}

mapper.delete:
delete from tb_exam
where id = #{id,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=INTEGER}

mapper.insert:
insert into tb_exam (id, group_id, `name`, 
  `type`, score, begin_time, 
  end_time, publisher_id, `status`
  )
values (#{id,jdbcType=VARCHAR}, #{groupId,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, 
  #{type,jdbcType=BIT}, #{score,jdbcType=VARCHAR}, #{beginTime,jdbcType=TIMESTAMP}, 
  #{endTime,jdbcType=TIMESTAMP}, #{publisherId,jdbcType=INTEGER}, #{status,jdbcType=INTEGER}
  )

mapper.update:
update tb_exam
set `name` = #{name,jdbcType=VARCHAR},
  `type` = #{type,jdbcType=BIT},
  score = #{score,jdbcType=VARCHAR},
  begin_time = #{beginTime,jdbcType=TIMESTAMP},
  end_time = #{endTime,jdbcType=TIMESTAMP},
  publisher_id = #{publisherId,jdbcType=INTEGER},
  `status` = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}
  and group_id = #{groupId,jdbcType=INTEGER}
